import mongoose from "mongoose";
import bcrypt from 'bcryptjs'

const passPortUserSchema= mongoose.Schema({
    email: String,
    password: String
})

passPortUserSchema.pre('save', async function(next){
    const  passportUser= this;

    const hash = await bcrypt.hash(passportUser.password, 10);

    passportUser.password = hash;
    next()
});

passPortUserSchema.methods.isValidPassword= async function(password){
    const user= this;
    const compare= await bcrypt.compare(password, user.password);
    return compare
}

const passPortModel= mongoose.model('passPortUser', passPortUserSchema);


export default passPortModel

