sourcesContent:
import passport from "passport";
import local from 'passport-local';
import passPortModel from '../models/passPortUser'

const localStrategy = local.Strategy


passport.use('signup', new localStrategy(
    {
        usernameField: 'email',
        passwordField: 'password'
    },
    async (email, password, done) => {
        try {
            const passportUser = await passPortModel.create({ email, password });
            return done(null, passportUser)
        } catch (error) {
            done(error)
        }
    }
))


passport.use('login', new localStrategy(
    {
        usernameField: 'email',
        passwordField: 'password'
    }, 
    async (email, password, done)=>{
      try {
        const passportUser = await passPortModel.findOne({email});

        if(!passportUser) {
            return done(null, false, {message: 'user not found'});
        }

        const validate= await passportUser.isValidPassword(password);

        if(!validate) {
            return done(null, false, {message: 'invalid password'})
        }

        return done(null, passportUser, {message: 'logged in succesfully'})
      } catch (error) {
        return done(error)
      }
    }
));

