            return e.message
        }))
    } else {
        const blog = new Blog({
            title: req.body.title,
            content: req.body.content,
            image: req.body.image,
            likes: req.body.likes,
        })
        await blog.save();
        return res.send(blog)
    }
}

export default postBlog

